sys_script_include.script:
var processLeagueStanding = function(uniqueSysId) {
	var gr = new GlideRecord('x_87436_laliga_dailyapitransactions');
	gr.query('sys_id', uniqueSysId);
	gr.next();
	var objIn = JSON.parse(gr.apiresponse);
	gs.addInfoMessage(gr.apiresponse);
	gs.addInfoMessage(objIn.records.length);

	for (var i = 0 ; i < objIn.records.length ; i++){
		gs.addInfoMessage(objIn.records[i].team);
		var team = new GlideRecord('x_87436_laliga_laliga_teams');
		team.query('name',objIn.records[i].team);
	}
};
