sys_script_include.script:
var processLeagueStanding = function(uniqueSysId) {
	var gr = new GlideRecord('x_87436_laliga_dailyapitransactions');
	gr.query('sys_id', uniqueSysId);
	gr.next();
	var objIn = JSON.parse(gr.apiresponse);
	for (var i = 0 ; i < objIn.records.length ; i++){
		var team = new GlideRecord('x_87436_laliga_laliga_teams');
		team.addQuery('name',objIn.records[i].team);
		team.query();
		var teamRowCount = team.getRowCount();
		if(teamRowCount === 1){
			gs.addInfoMessage("1");
			while(team.next()){
			gs.addInfoMessage("2");
			var scoreUpdate = new GlideRecord('x_87436_laliga_laliga_league_table');
			scoreUpdate.addQuery('team_name', team.getValue('name'));
			scoreUpdate.query();
			gs.addInfoMessage(scoreUpdate.getEncodedQuery());
			gs.addInfoMessage(scoreUpdate.getRowCount());
			scoreUpdate.next();
			if(scoreUpdate.getRowCount() == 1) {
				gs.info(scoreUpdate.team_name + ' exists');
				scoreUpdate.matches_played = objIn.records[i].played;
				scoreUpdate.wins = objIn.records[i].win;
				scoreUpdate.loss = objIn.records[i].loss;
				scoreUpdate.draws = objIn.records[i].draw;
				scoreUpdate.goals_against = objIn.records[i].goalsAgainst;
				scoreUpdate.goals_for = objIn.records[i].goalsFor;
				scoreUpdate.points = objIn.records[i].points;
				scoreUpdate.update();
			}
		}}
		else if (teamRowCount === 0){
			var teamCreate = new GlideRecord('x_87436_laliga_laliga_teams');
			teamCreate.newRecord();
			teamCreate.name = objIn.records[i].team;
			teamCreate.insert();
			var teamCreatedSysId = teamCreate.sysid;
			var scoreCreate = new GlideRecord('x_87436_laliga_laliga_league_table');
			scoreCreate.team_name = objIn.records[i].team;
			scoreCreate.matches_played = objIn.records[i].played;
			scoreCreate.wins = objIn.records[i].win;
			scoreCreate.loss = objIn.records[i].loss;
			scoreCreate.draws = objIn.records[i].draw;
			scoreCreate.goals_against = objIn.records[i].goalsAgainst;
			scoreCreate.goals_for = objIn.records[i].goalsFor;
			//scoreCreate.points = objIn.records[i].points;
			scoreCreate.insert();
		}
	}
};
